import React, {Component} from "react";
import './Counter.css';

class Counter extends Component{

    //define the initial state in a constructor
    //state => counter = 0

    constructor() {
        super(); //need to call it super before using it
        this.state = {
            counter : 0,
            secondCounter : 100
        }

        //bind method
        this.increment = this.increment.bind(this);
    }

    // DOM in js, document, object, model, very complex and slow
    // Virtual dom from react, faster (only update what changed instead of all)
    render(){
    return(
        <div className="Counter">
        <button onClick = {this.increment}>+1</button>
        <span className="count">{this.state.counter}</span>
        <span className="count">{this.state.secondCounter}</span>
        </div>
        )
    }

    increment() {
        console.log('increment called');
        this.setState(
            {
                counter : this.state.counter + 1,
                secondCounter : this.state.secondCounter + 1
            }
        );
    }
}

export default Counter;
