import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Routes, Link} from 'react-router-dom'
import withNavigation from './WithNavigation'
import withParams from './withParams'

class TodoApp extends Component {
    render() {
        const LoginComponentWithNavigation = withNavigation(LoginComponent)
        const WelcomeComponentWithParams = withParams(WelcomeComponent)
        return (
            <div className = "TodoApp">
                <Router>
                    <HeaderComponent/>
                    <Routes>
                        <Route path = "/" element={<LoginComponentWithNavigation/>}/>
                        <Route path = "/login" element = {<LoginComponentWithNavigation/>}/>
                        <Route path = "/todos" element = {<ListTodoComponent/>}/>
                        <Route path = "/welcome/:name" element = {<WelcomeComponentWithParams/>}/>  
                        <Route path = "*" element = {<ErrorComponent/>}/>
                    </Routes>
                    <FooterComponent/>
                </Router>
                {/*<LoginComponent/>
                <WelcomeComponent/>*/}
            </div>
        )
    }
}

class WelcomeComponent extends Component {
    render() {
        return (
            <div>
                <div>Welcome {this.props.params.name}</div>
                <div>You can manage your todos <Link to = "/todos">here</Link>.</div>
            </div>
        )
    }
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: 'userName',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    handleChange(event) {
        //console.log(event.target.value);
        //this.setState({username:event.target.value})
        //event.target.name is a variable, left side tends to be a constant, so use []
        this.setState({[event.target.name]:event.target.value})
    }

    loginClicked(){
        //admin, 123456
        if(this.state.username === 'admin' && this.state.password==='123456'){
            this.props.navigate(`/welcome/${this.state.username}`)
            this.setState({showSuccessMessage:true})
            this.setState({hasLoginFailed:false})
        }
        else{
            this.setState({showSuccessMessage:false})
            this.setState({hasLoginFailed:true})
        }
    }

    render() {
        return(
            <div>
                {/*<ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/>*/}
                {/*<ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage}/>*/}

                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
                {this.state.showSuccessMessage && <div>Login Successfully</div>}
                User Name: <input type = "text" name = "username" value = {this.state.username} onChange = {this.handleChange}/>
                Password: <input type = "password" name = "password" value = {this.state.password} onChange = {this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }
}

class ListTodoComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            todos : 
            [
                {id: 1, description : 'Learn React', done: false, targetDate: new Date()},
                {id: 2, description : 'Become an expert at React', done: false, targetDate: new Date()},
                {id: 3, description : 'Do more React', done: false, targetDate: new Date()}
            ]
        }
    }
    render () {
        return (
            <div>
                <h1>List Todos</h1>
                <table>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>description</th>
                            <th>Target Date</th>
                            <th>Is Completed</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map (
                                todo => 
                                    <tr key={todo.id}>
                                        <td>{todo.id}</td>
                                        <td>{todo.description}</td>
                                        <td>{todo.targetDate.toString()}</td>
                                        <td>{todo.done.toString()}</td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        return (
            <header>
                <nav className="navbar navbar-expend-md navbar-dark bg-dark">
                    <div><a herf="www.google.com" className="navbar-bright">somelink</a></div>
                    <ul className="navbar-nav">
                        <li className="nav-link"><Link to="/welcome">Home</Link></li>
                        <li className="nav-link"><Link to="/todos">Todos</Link></li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-link"><Link to="/login">Login</Link></li>
                        <li className="nav-link"><Link to="/logout">Logout</Link></li>
                    </ul>
                </nav>
            </header>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return (
            <div>
                <hr/>Footer
            </div>
        )
    }
}

function ErrorComponent() {
    return <div>Error</div>
}

function ShowInvalidCredentials(props) {
    if(props.hasLoginFailed) {
        return <div>Invalid Credentials</div>
    }
    return null
}

function ShowLoginSuccessMessage(props) {
    if(props.showSuccessMessage) {
        return <div>Logined Successfully</div>
    }
    return null
}

export default TodoApp
